import React from 'react';

const Dropdown = ({ options }) => {
    const renderedOptions = options.map((option) => {
        return(
            <div key={option.value}>
                {option.label}
            </div>
        );
    });

    return(
        <div className="ui form">
            <div className="field">
                <label className="label">Select a color</label>
            </div>
        </div>
    );
};

export default Dropdown;
